# Declare WDL version 1.0 if working in Terra
import "RNA_seq_pipeline.wdl" as rna_seq

workflow RNA_Seq_Operend_workflow {
	# we're all sharing the same Operend settings so
	# might as well just have to config one set in the inputs file
    String cromwell_helper_docker
	String operend_core_base_URL
	String operend_api_token_secret
	String operend_api_verify_https
	String operend_fastq_query


    call getInputTsv{input: docker_image=cromwell_helper_docker,operend_core_base_URL=operend_core_base_URL,
			operend_api_token_secret=operend_api_token_secret,operend_api_verify_https=operend_api_verify_https,
			operend_fastq_query=operend_fastq_query
			}
    scatter(row in getInputTsv.output_table){
        call copyFastQs{input: docker_image=cromwell_helper_docker,R1_id=row.R1_ID,R2_id=row.R2_ID,
			R1_name=row.R1_NAME,R2_name=row.R2_NAME,operend_core_base_URL=operend_core_base_URL,
			operend_api_token_secret=operend_api_token_secret,operend_api_verify_https=operend_api_verify_https
			}
        call rna_seq.rnaseq_pipeline_workflow{input: fastq1=copyFastQs.R1,fastq2=copyFastQs.R2}
    }
}
task copyFastQs{
    String docker_image
	String operend_core_base_URL
	String operend_api_token_secret
	String operend_api_verify_https
    String R1_id
    String R2_id
    String R1_name
    String R2_name

    command {
        mkdir outter2
        python3 /operend_scripts/get_workfile.py "${operend_core_base_URL}" "${operend_api_token_secret}" "${operend_api_verify_https}" "${R1_id}" "outter2"
        python3 /operend_scripts/get_workfile.py "${operend_core_base_URL}" "${operend_api_token_secret}" "${operend_api_verify_https}" "${R2_id}" "outter2"
        ls /operend_scripts
        pwd


    }
    runtime {

    docker: docker_image
    cpu: 2
    preemptible: 0
    disks: "local-disk 100 HDD"
    memory: "1 GB"
  }
  output {
    File R1 = "outter2/${R1_name}"
    File R2 = "outter2/${R2_name}"
  }
}
task getInputTsv {
    Int preemptible = 0
    Int cpu = 1
	String docker_image
	String operend_core_base_URL
	String operend_api_token_secret
	String operend_api_verify_https
	String operend_fastq_query
    command {
        mkdir outter
        python3 /operend_scripts/fastq_copy.py "${operend_core_base_URL}" "${operend_api_token_secret}" "${operend_api_verify_https}" "${operend_fastq_query}" "outter"
        ls /operend_scripts
        pwd

    }


  runtime {
    docker: docker_image
    cpu: cpu
    preemptible: preemptible
    disks: "local-disk 100 HDD"
    memory: "1 GB"
  }
    output {
         Array[Object] output_table =read_objects("outter/this_tsv_file.tsv")
    }
}

imports.https://api.firecloud.org/ga4gh/v1/tools:
task rnaseqc2 {

    File bam_file
    File genes_gtf
    String sample_id
    String? strandedness 
    File? intervals_bed
    String? flags

    Int memory
    Int disk_space
    Int num_threads
    Int num_preempt

    command {
        set -euo pipefail
        echo $(date +"[%b %d %H:%M:%S] Running RNA-SeQC 2")
        touch ${sample_id}.fragmentSizes.txt
        rnaseqc ${genes_gtf} ${bam_file} . -s ${sample_id} ${"--bed " + intervals_bed} ${"--stranded " + strandedness} -vv ${flags}
        echo "  * compressing outputs"
        gzip *.gct
        echo $(date +"[%b %d %H:%M:%S] done")
    }

    output {
        File gene_tpm = "${sample_id}.gene_tpm.gct.gz"
        File gene_counts = "${sample_id}.gene_reads.gct.gz"
        File exon_counts = "${sample_id}.exon_reads.gct.gz"
        File metrics = "${sample_id}.metrics.tsv"
        File insertsize_distr = "${sample_id}.fragmentSizes.txt"
    }

    runtime {
        docker: "gcr.io/broad-cga-francois-gtex/gtex_rnaseq:V10"
        memory: "${memory}GB"
        disks: "local-disk ${disk_space} HDD"
        cpu: "${num_threads}"
        preemptible: "${num_preempt}"
    }

    meta {
        author: "Francois Aguet"
    }
}


workflow rnaseqc2_workflow {
    call rnaseqc2
}

