imports./usr2/collab/dulrich/development/bulk-rn:
task markduplicates {

    File input_bam
    String prefix
    Int? max_records_in_ram
    Float? sorting_collection_size_ratio

    Float memory
    Int java_memory = floor(memory - 0.5)
    Int disk_space
    Int num_threads
    Int num_preempt

    String output_bam = sub(basename(input_bam), "\\.bam$", ".md.bam")

    command {
        set -euo pipefail
		java -XX:ParallelGCThreads=${num_threads} -jar -Xmx${java_memory}g /opt/picard-tools/picard.jar MarkDuplicates -I ${input_bam} -O ${output_bam} -PROGRAM_RECORD_ID null -MAX_RECORDS_IN_RAM ${max_records_in_ram} -SORTING_COLLECTION_SIZE_RATIO ${sorting_collection_size_ratio} -TMP_DIR . -M ${prefix} -ASSUME_SORT_ORDER coordinate -TAGGING_POLICY DontTag -OPTICAL_DUPLICATE_PIXEL_DISTANCE 100

        samtools index ${output_bam} -@ ${num_threads} 
    }

    output {
        File bam_file = "${output_bam}"
        File bam_index = "${output_bam}.bai"
        File metrics = "${prefix}.marked_dup_metrics.txt"
    }

    runtime {
        docker: "gcr.io/broad-cga-francois-gtex/gtex_rnaseq:V10"
        memory: "${memory}GB"
        disks: "local-disk ${disk_space} HDD"
        cpu: "${num_threads}"
        preemptible: "${num_preempt}"
    }

    meta {
        author: "Francois Aguet"
    }
}


workflow markduplicates_workflow {
    call markduplicates
}

